            const dom = new XmlDocument();
            try {
                dom.parse(xml);
            }
            catch (e) {
                throw new UnsupportedFormatError('Unsupported format', e);
            }
            this._score = new Score();
            this._score.stylesheet.hideDynamics = true;
            this._parseDom(dom);
            ModelUtils.consolidate(this._score);
